#include <stdint.h>

#include "GPIOxDriver.h"

#define SUMA '+'
#define RESTA '-'
#define MULTIPLICACION '*'
#define DIVISION '/'
#define FACTORIAL '!'
#define FIBONACCI 'F'

//Funcion switch case

uint16_t resultadoOperacion(uint8_t tipoDeOperacion,uint8_t numeroA, uint8_t numeroB){
	uint16_t resultado = 0;

	switch (tipoDeOperacion){
	case SUMA:
	{
		resultado= numeroA + numeroB;
		break;
	}
	case RESTA:
	{
		resultado= numeroA - numeroB;
		break;
	}
	default:
	{
		resultado = 0;
		break;
	}
	}
	return resultado;
}


