uint32_t resultadoOperaciones(uint8_t tipoDeOperacion, uint8_t numero){
	uint16_t contador = numero;
	uint16_t resultado = 1;

	switch (tipoDeOperacion){
	case FACTORIAL:
	{
		while (contador>0){

			resultado *= contador;
			contador --;
		}
		break;
	}
	case FIBONACCI:
	{
		while (contador<numero){
			contador++;
		}
		break;
	}
	default:
	{
		resultado = 0;
		break;
	}
	}
	return resultado;
}







